
public class Student {
	private int id;
	private String name;
	private int age;
	public Student() {
		// TODO Auto-generated constructor stub
	}
	
	public int getId()
	{
		return this.id;
	}
	
	public String getName()
	{
		return this.name;
	}
	
	public int getAge()
	{
		return this.age;
	}
	
	public void setId(int x)
	{
		this.id = x;
	}
	
	public void setName(String y)
	{
		this.name = y;
	}
	
	public void setAge(int z)
	{
		this.age = z;
	}
	

}
